package src.guava;import com.google.common.base.Objects;import com.google.common.base.Strings;/** * @Description: * @Date: 2020/4/13 17:01 * @Auther: zql */public class GuavaTest {    public static void main(String[] args) {        /**         * Strings.isNullOrEmpty():判断字符串为空         */        String input = null;        boolean nullOrEmpty = Strings.isNullOrEmpty(input);        System.out.println(nullOrEmpty);        /**         * Object:判断两个对象是否相等         */        Object a =null;        Object b = new Object();        Objects.equal(a,b);    }}